import React, { useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const App = () => {
  const [files, setFiles] = useState([]);
  const [jobDesc, setJobDesc] = useState("");
  const [resumeList, setResumeList] = useState([]);
  const [loading, setLoading] = useState(false);
  const jobTemplates = [
    "Software Engineer - Java, Python, SQL",
    "Data Analyst - Python, SQL, Tableau",
    "Frontend Developer - React, JavaScript, CSS,",
    ""
  ];

  const handleFileChange = (event) => {
    setFiles(event.target.files);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!files.length || !jobDesc) return alert("Please upload at least one resume and enter a job description");

    setLoading(true);
    const newResumeList = [];

    for (let file of files) {
      const formData = new FormData();
      formData.append("resume", file);
      formData.append("job_desc", jobDesc);

      try {
        const response = await axios.post("http://127.0.0.1:5001/shortlist", formData, {
          headers: { "Content-Type": "multipart/form-data" },
        });
        newResumeList.push({ name: file.name, match_score: response.data.match_score, url: URL.createObjectURL(file) });
      } catch (error) {
        alert(`Failed to upload ${file.name}`);
      }
    }

    setResumeList([...resumeList, ...newResumeList].sort((a, b) => b.match_score - a.match_score));
    setLoading(false);
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">AI Resume Shortlisting</h2>
      <form onSubmit={handleSubmit} className="mt-4">
        <div className="mb-3">
          <label className="form-label">Upload Resumes (PDFs)</label>
          <input type="file" className="form-control" onChange={handleFileChange} accept=".pdf" multiple required />
        </div>
        <div className="mb-3">
          <label className="form-label">Job Description</label>
          <select className="form-select" onChange={(e) => setJobDesc(e.target.value)}>
            <option value="">Select a Job Template</option>
            {jobTemplates.map((template, index) => (
              <option key={index} value={template}>{template}</option>
            ))}
          </select>
          <textarea className="form-control mt-2" rows="4" placeholder="Or enter manually..." onChange={(e) => setJobDesc(e.target.value)} required></textarea>
        </div>
        <button type="submit" className="btn btn-primary w-100" disabled={loading}>{loading ? "Processing..." : "Submit"}</button>
      </form>

      {resumeList.length > 0 && (
        <div className="mt-4">
          <h4>Shortlisted Resumes</h4>
          <ul className="list-group">
            {resumeList.map((resume, index) => (
              <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                <div>
                  {resume.name} - Match Score: <strong>{resume.match_score}%</strong>
                </div>
                <a href={resume.url} target="_blank" rel="noopener noreferrer" className="btn btn-sm btn-success">Preview</a>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
