import React, { useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const App = () => {
  const [files, setFiles] = useState([]);
  const [jobDesc, setJobDesc] = useState("");
  const [resumeList, setResumeList] = useState([]);
  const [loading, setLoading] = useState(false);
  const jobTemplates = [
    "Software Engineer - Java, Python, SQL",
    "Data Analyst - Python, SQL, Tableau",
    "Frontend Developer - React, JavaScript, CSS,",
    "Embedded Systems Engineer - C, C++, Assembly",
    "Cloud Engineer - AWS, Azure, Google Cloud",
    "VLSI Engineer - Verilog, VHDL, Python",
    "Cyber Security Engineer - Python, C, C++,",
    "DevOps Engineer - Python, Ansible, Jenkins",
    "Full Stack Developer - Python, JavaScript, CSS,",
    "Network Engineer - Python, C, C++,",
    "Python Developer - Python, SQL,",
    "C++ Developer - C++, Python, SQL",
    "IoT Engineer - C, C++, Python, MQTT, Raspberry Pi, Arduino",
    "DevOps Engineer - Docker, Kubernetes, Jenkins, Ansible, Terraform",
    "Blockchain Developer - Solidity, Ethereum, Hyperledger, Smart Contracts",
    "Game Developer - C++, C#, Unity, Unreal Engine",
    "Networking Engineer - CCNA, TCP/IP, Routing, Switching, Firewalls",
    "AI Engineer - Python, Deep Learning, NLP, Computer Vision, TensorFlow",
    "AR/VR Developer - Unity, Unreal Engine, C#, C++, OpenXR",
    "Mobile App Developer - Java, Kotlin, Swift, Flutter, React Native",
    "Software Engineer - Java, C++, Python, SQL, Software Development Lifecycle",
    "Database Administrator - MySQL, PostgreSQL, MongoDB, Oracle, SQL Server",
    "Electrical Engineer - MATLAB, Simulink, Circuit Design, Embedded C",
    "Mechanical Engineer - SolidWorks, AutoCAD, MATLAB, ANSYS",
    "Civil Engineer - AutoCAD, Revit, STAAD.Pro, SAP2000",

];

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Resume Submitted:', file);
    console.log('Job Description:', jobDesc || template);
  };

  return (
    <div className="container">
      <h1>AI Resume Shortlisting</h1>

      {/* File Upload */}
      <label htmlFor="resume"><strong>Upload Resume (PDF)</strong></label>
      <input
        type="file"
        id="resume"
        accept=".pdf"
        onChange={handleFileChange}
      />

      {/* Job Description Dropdown */}
      <label htmlFor="template"><strong>Job Description</strong></label>
      <select id="template" onChange={(e) => setTemplate(e.target.value)}>
        <option value="">Select a Job Template</option>
        <option value="Software Engineer">Software Engineer</option>
        <option value="Data Scientist">Data Scientist</option>
        <option value="Product Manager">Product Manager</option>
      </select>

      {/* Manual Job Description */}
      <textarea
        placeholder="Or enter manually..."
        value={jobDesc}
        onChange={(e) => setJobDesc(e.target.value)}
      />

      {/* Submit Button */}
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
};

export default App;
