import React, { useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const App = () => {
  const [file, setFile] = useState(null);
  const [jobDesc, setJobDesc] = useState("");
  const [responseMessage, setResponseMessage] = useState("");

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!file || !jobDesc) {
      setResponseMessage("Please upload a resume and enter a job description.");
      return;
    }

    const formData = new FormData();
    formData.append("resume", file);
    formData.append("job_desc", jobDesc);

    try {
      const response = await axios.post("http://127.0.0.1:5001/shortlist", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setResponseMessage(`Resume Submitted! Match Score: ${response.data.match_score}%`);
    } catch (error) {
      setResponseMessage("Failed to upload resume.");
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">AI Resume Shortlisting</h2>
      <form onSubmit={handleSubmit} className="mt-4">
        <div className="mb-3">
          <label className="form-label">Upload Resume (PDF)</label>
          <input type="file" className="form-control" onChange={handleFileChange} accept=".pdf" required />
        </div>
        <div className="mb-3">
          <label className="form-label">Job Description</label>
          <textarea className="form-control" rows="4" onChange={(e) => setJobDesc(e.target.value)} required></textarea>
        </div>
        <button type="submit" className="btn btn-primary w-100">Submit</button>
      </form>
      {responseMessage && <div className="alert alert-info mt-3">{responseMessage}</div>}
    </div>
  );
};

export default App;
