import React, { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

toast.configure();

const UploadResume = () => {
  const [file, setFile] = useState(null);
  const [jobDesc, setJobDesc] = useState("");

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file || !jobDesc) {
      toast.error("Please upload a resume and enter job description");
      return;
    }

    const formData = new FormData();
    formData.append("resume", file);
    formData.append("job_desc", jobDesc);

    try {
      const response = await axios.post("http://127.0.0.1:5001/shortlist", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      toast.success(response.data.message);
    } catch (error) {
      toast.error("Failed to upload resume");
    }
  };

  return (
    <div className="card p-4 mt-4 shadow-lg">
      <h4>Upload Resume</h4>
      <form onSubmit={handleUpload}>
        <input type="file" className="form-control mb-3" onChange={(e) => setFile(e.target.files[0])} />
        <textarea
          className="form-control mb-3"
          rows="3"
          placeholder="Enter Job Description"
          value={jobDesc}
          onChange={(e) => setJobDesc(e.target.value)}
        ></textarea>
        <button type="submit" className="btn btn-primary w-100">Upload</button>
      </form>
    </div>
  );
};

export default UploadResume;
