import React, { useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const App = () => {
  const [file, setFile] = useState(null);
  const [jobDesc, setJobDesc] = useState("");
  const [resumeList, setResumeList] = useState([]);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!file || !jobDesc) return alert("Please upload a resume and enter a job description");

    const formData = new FormData();
    formData.append("resume", file);
    formData.append("job_desc", jobDesc);

    try {
      const response = await axios.post("http://127.0.0.1:5001/shortlist", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      setResumeList([...resumeList, { name: file.name, match_score: response.data.match_score }]);
    } catch (error) {
      alert("Failed to upload resume.");
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">AI Resume Shortlisting</h2>
      <form onSubmit={handleSubmit} className="mt-4">
        <div className="mb-3">
          <label className="form-label">Upload Resume (PDF)</label>
          <input type="file" className="form-control" onChange={handleFileChange} accept=".pdf" required />
        </div>
        <div className="mb-3">
          <label className="form-label">Job Description</label>
          <textarea className="form-control" rows="4" onChange={(e) => setJobDesc(e.target.value)} required></textarea>
        </div>
        <button type="submit" className="btn btn-primary w-100">Submit</button>
      </form>

      {resumeList.length > 0 && (
        <div className="mt-4">
          <h4>Uploaded Resumes</h4>
          <ul className="list-group">
            {resumeList.map((resume, index) => (
              <li key={index} className="list-group-item">
                {resume.name} - Match Score: <strong>{resume.match_score}%</strong>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
