import React, { useState } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const App = () => {
  const [file, setFile] = useState(null);
  const [jobDesc, setJobDesc] = useState("");
  const [result, setResult] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleJobDescChange = (e) => {
    setJobDesc(e.target.value);
  };

  const handleUpload = async () => {
    if (!file || !jobDesc) {
      toast.error("Please select a file and enter job description");
      return;
    }

    const formData = new FormData();
    formData.append("resume", file);
    formData.append("job_desc", jobDesc);

    try {
      const response = await axios.post("http://127.0.0.1:5001/shortlist", formData);
      setResult(response.data);
      toast.success("Resume processed successfully!");
    } catch (error) {
      toast.error("Failed to upload resume");
    }
  };

  return (
    <div className="container">
      <h2>AI Resume Shortlisting</h2>
      <input type="file" className="form-control" onChange={handleFileChange} />
      <textarea className="form-control mt-2" placeholder="Enter Job Description" onChange={handleJobDescChange}></textarea>
      <button className="btn btn-primary w-100 mt-3" onClick={handleUpload}>Upload Resume</button>

      {result && (
        <div className="mt-3">
          <h4>Results:</h4>
          <p><strong>Filename:</strong> {result.filename}</p>
          <p><strong>Match Score:</strong> {result.match_score}%</p>
        </div>
      )}

      <ToastContainer />
    </div>
  );
};

export default App;
