import React, { useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const App = () => {
  const [files, setFiles] = useState([]);
  const [file, setFile] = useState(null);
  const [jobDesc, setJobDesc] = useState("");
  const [template, setTemplate] = useState("");
  const [loading, setLoading] = useState(false);

  const jobTemplates = [
    "Software Engineer - Java, Python, SQL",
    "Data Analyst - Python, SQL, Tableau",
    "Frontend Developer - React, JavaScript, CSS",
    "Embedded Systems Engineer - C, C++, Assembly",
    "Cloud Engineer - AWS, Azure, Google Cloud",
    "VLSI Engineer - Verilog, VHDL, Python",
    "Cyber Security Engineer - Python, C, C++",
    "DevOps Engineer - Docker, Kubernetes, Ansible",
    "Blockchain Developer - Solidity, Ethereum, Hyperledger",
    "Game Developer - C++, C#, Unity, Unreal Engine",
    "AI Engineer - Python, Deep Learning, NLP, TensorFlow",
    "Mobile App Developer - Java, Kotlin, Swift, Flutter",
    "Database Administrator - MySQL, PostgreSQL, MongoDB",
    "Electrical Engineer - MATLAB, Simulink, Embedded C",
    "Mechanical Engineer - SolidWorks, AutoCAD, ANSYS",
    "Civil Engineer - AutoCAD, Revit, STAAD.Pro, SAP2000",
  ];

  // Handle multiple file uploads
  const handleFileChange = (event) => {
    setFiles([...event.target.files]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Resumes Submitted:', files);
    console.log('Job Description:', jobDesc || template);
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">AI Resume Shortlisting</h1>

      {/* File Upload */}
      <div className="form-group">
        <label htmlFor="resume"><strong>Upload Resumes (PDF)</strong></label>
        <input
          type="file"
          id="resume"
          accept=".pdf"
          multiple
          className="form-control"
          onChange={handleFileChange}
        />
      </div>

      {/* Job Description Dropdown */}
      <div className="form-group mt-3">
        <label htmlFor="template"><strong>Job Description</strong></label>
        <select
          id="template"
          className="form-control"
          onChange={(e) => setTemplate(e.target.value)}
        >
          <option value="">Select a Job Template</option>
          {jobTemplates.map((job, index) => (
            <option key={index} value={job}>
              {job}
            </option>
          ))}
        </select>
      </div>

      {/* Manual Job Description */}
      <div className="form-group mt-3">
        <textarea
          className="form-control"
          placeholder="Or enter manually..."
          value={jobDesc}
          onChange={(e) => setJobDesc(e.target.value)}
        />
      </div>

      {/* Submit Button */}
      <div className="text-center mt-4">
        <button
          className="btn btn-primary"
          onClick={handleSubmit}
        >
          Submit
        </button>
      </div>
    </div>
  );
};

export default App;
